package com.ombasa;import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.assertEquals;class CryptorTest {    private Cryptor enCryptor;    @BeforeEach    void setUp() {        enCryptor = new Cryptor();    }    @Test    void encrypt() {        enCryptor.setSteps(3);        assertEquals("QEB NRFZH YOLTK CLU GRJMP LSBO QEB IXWV ALD", enCryptor.encrypt("THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG"));        enCryptor.setSteps(1);        assertEquals("A@#", enCryptor.encrypt("B@#"));        assertEquals("", enCryptor.encrypt(""));        assertEquals(null, enCryptor.encrypt(null));    }    @Test    void descrypt() {        enCryptor.setSteps(3);        assertEquals("THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG", enCryptor.descrypt("QEB NRFZH YOLTK CLU GRJMP LSBO QEB IXWV ALD"));        enCryptor.setSteps(1);        assertEquals("", enCryptor.descrypt(""));        assertEquals(null, enCryptor.encrypt(null));    }}